var link=document.querySelector(".btn-address"),writeus=document.querySelector(".writeus"),close=writeus.querySelector(".writeus-close"),form=writeus.querySelector("form"),login=writeus.querySelector("[name=login]"),mail=writeus.querySelector("[name=email]"),text=writeus.querySelector("[name=textarea]"),storage=localStorage.getItem("login");link.addEventListener("click",function(e){e.preventDefault(),writeus.classList.add("modal-show"),login.focus()}),close.addEventListener("click",function(e){e.preventDefault(),writeus.classList.remove("modal-show"),writeus.classList.remove("modal-error")}),window.addEventListener("keydown",function(e){27==e.keyCode&&writeus.classList.contains("modal-show")&&(writeus.classList.remove("modal-show"),writeus.classList.remove("modal-error"))}),form.addEventListener("submit",function(e){login.value&&mail.value&&text.value?localStorage.setItem("login",login.value):(e.preventDefault(),writeus.classList.add("modal-error"))});